import React, { useState } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router';

import userLoginFetch from 'redux/actions/auth';

import './login.scss';

function Login({ userLoginFetch, currentUser, error, isFetching }) {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        userLoginFetch({ username, password });
    };

    const isInvalid = username === '' || password === '';

    if (currentUser) {
        return <Redirect to="/profile" />
    }

    return (
        <main className="main">
            <div className="container">
                <div className="auth">
                    <h1>Авторизация</h1>
                    <p>Это бесплатно - и всегда так будет.</p>
                    <form className="login-form" onSubmit={handleSubmit}>
                        <div className="field field-group">
                            <input
                                className="control"
                                type="text"
                                id="username"
                                placeholder="Имя пользователя"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                            />
                        </div>
                        <div className="field field-group">
                            <input
                                className="control"
                                type="password"
                                id="password"
                                placeholder="Пароль"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>
                        <button
                            className="button button-primary"
                            type="submit"
                            disabled={isInvalid}
                        >
                            {isFetching ? 'Загрузка...' : 'Войти'}
                        </button>
                    </form>
                    {error && (<p className="alert" style={{ color: '#f44336' }}>{error}</p>)}
                </div>
            </div>
        </main>
    );
}

const mapStateToProps = (state) => ({
    currentUser: state.auth.currentUser,
    error: state.auth.error,
    isFetching: state.auth.isFetching,
});

const mapDispatchToProps = (dispatch) => ({
    userLoginFetch: (userInfo) => dispatch(userLoginFetch(userInfo)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Login);