import React, { useState } from 'react';
import { connect } from 'react-redux';

import { fetchUserPost } from 'redux/actions/auth';

function Register({ fetchUserPost }) {

    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);

    const handleSubmit = (event) => {
        event.preventDefault();
        fetchUserPost({ username, email, password })
    };

    const isInvalid = username === '' || email === '' || password === '';

    return (
        <main className="main">
            <div className="container">
                <div className="register">
                    <h1>Регистрация</h1>
                    <p>Это бесплатно - и всегда так будет.</p>
                    <form onSubmit={handleSubmit}>
                        <input
                            type="text"
                            placeholder="Логин"
                            value={username}
                            onChange={(event) => setUsername(event.target.value)}
                        />
                        <input
                            type="email"
                            placeholder="Электронный адрес"
                            value={email}
                            onChange={(event) => setEmail(event.target.value)}
                        />
                        <input
                            type="password"
                            placeholder="Пароль"
                            value={password}
                            onChange={(event) => setPassword(event.target.value)}
                        />
                        <button
                            type="submit"
                            disabled={isInvalid}
                        >
                            Продолжить регистрацию
                        </button>
                    </form>
                    {error && (<p>{error.message}</p>)}
                </div>
            </div>
        </main>
    );
}

const mapDispatchToProps = (dispatch) => ({
    fetchUserPost: (userInfo) => dispatch(fetchUserPost(userInfo))
});

export default connect(null, mapDispatchToProps)(Register);