import * as types from 'redux/constants/types/auth';
import initialState from 'redux/constants/initialState';

const auth = (state = initialState, { type, payload }) => {
    switch (type) {
        case types.AUTH_CHANGE_USERNAME:
            return { ...state, username: payload };
        case types.AUTH_CHANGE_PASSWORD:
            return { ...state, password: payload };
        case types.AUTH_CHANGE_VALID_USERNAME:
            return { ...state, isValidUsername: payload };
        case types.AUTH_CHANGE_VALID_PASSWORD:
            return { ...state, isValidPassword: payload };
        case types.AUTH_SET_IS_AUTH_VALUE:
            return { ...state, isAuthValue: payload };
        default:
            return state;
    }
};

export default auth;