import initialState from 'redux/constants/initialState';
import types from 'redux/constants/types/auth';

export default (state = initialState.auth, { type, payload }) => {
    switch (type) {
        case types.request:
            return { ...state, isFetching: true }
        case types.success:
            return { ...state, isFetching: false, currentUser: payload };
        case types.failure:
            return { ...state, isFetching: false, error: payload.massege }
        default:
            return state;
    }
};