import initialState from 'redux/constants/initialState';
import types from 'redux/constants/types/news';

export default (state = initialState.news, { type, payload }) => {
    switch (type) {
        case types.request:
            return { ...state, isFetching: true };
        case types.success:
            return { ...state, isFetching: false, data: payload };
        case types.failure:
            return { ...state, isFetching: false, error: payload };
        default:
            return state;
    }
};